package io.himoura.interview.leetcode.challenge.may;

import static org.hamcrest.MatcherAssert.assertThat;
import static org.hamcrest.Matchers.contains;
import static org.hamcrest.Matchers.emptyCollectionOf;

import java.util.List;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import io.himoura.interview.leetcode.challenge.may.Day17;

class Day17Test {

	@DisplayName("LeetCode example")
	@Test
	void shouldReturnLeetCodeResult() {

		// Given
		final Day17 day17 = new Day17();

		// When
		List<Integer> result1 = day17.findAnagrams("cbaebabacd", "abc");
		List<Integer> result2 = day17.findAnagrams("abab", "ab");

		// Then
		assertThat(result1, contains(0, 6));
		assertThat(result2, contains(0, 1, 2));
	}

	@DisplayName("Edge case")
	@Test
	void shouldReturnResult() {

		// Given
		final Day17 day17 = new Day17();

		// When
		List<Integer> resultHaveSameLength = day17.findAnagrams("bac", "abc");
		List<Integer> resultGreaterThanS = day17.findAnagrams("bac", "abcd");
		List<Integer> resultNoMatchAnagram = day17.findAnagrams("bac", "ef");

		// Then
		assertThat(resultHaveSameLength, contains(0));
		assertThat(resultGreaterThanS, emptyCollectionOf(Integer.class));
		assertThat(resultNoMatchAnagram, emptyCollectionOf(Integer.class));
	}

	@DisplayName("Avoid time out")
	@Test
	void shouldReturnResultInTime() {

		// Given
		final Day17 day17 = new Day17();
		String largeInput = ""
				+ "";
		// When
		List<Integer> resultHaveSameLength = day17.findAnagrams(largeInput, "abc");

		// Then
	}

}
